package basics;

public class Kleine1x1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//
		// Kleines 1x1
	    for (int i = 1; i <= 10; i++) {
	    	System.out.print((0+i)+ "er:" + "\t");
	      for (int x = 1; x <= 10; x++) {
	    	  System.out.print(i*x);
	    	  System.out.print("\t");
	     
	      }
	      System.out.print("\n");
	    }
	   
      
	}
	
	}

	
